                    SET countt = 0;
                ELSEIF(seq = che) then
                    SET countt = countt + 1;
                    INSERT INTO T_ATCH_FILE_DETAIL (ATCH_FILE_ID,FILE_SN,FILE_STRE_COURS,STRE_FILE_NM,ORIGN_FILE_NM,FILE_EXTSN)
